import { useState } from 'react';

function RoommateSearch() {
  const [filters, setFilters] = useState({
    university: '',
    moveInDate: '',
    priceRange: '',
    lifestyle: [],
  });

  // Mock data - replace with real API data
  const roommates = [
    {
      id: 1,
      name: "Sarah Johnson",
      age: 20,
      university: "State University",
      interests: ["Reading", "Hiking", "Photography"],
      moveInDate: "Fall 2024",
      image: "https://placeholder.com/150",
    },
    // Add more mock data
  ];

  return (
    <div className="pt-[72px]">
      {/* Search Header */}
      <div className="bg-gradient-to-br from-blue-600 via-blue-700 to-indigo-800 py-12">
        <div className="container mx-auto px-6">
          <h1 className="text-4xl font-bold text-white mb-6">Find Your Perfect Roommate</h1>

          {/* Filters */}
          <div className="bg-white/95 backdrop-blur-lg p-6 rounded-xl shadow-lg">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <input
                type="text"
                placeholder="University"
                className="p-3 border rounded-lg"
                value={filters.university}
                onChange={(e) => setFilters({...filters, university: e.target.value})}
              />
              <input
                type="date"
                className="p-3 border rounded-lg"
                value={filters.moveInDate}
                onChange={(e) => setFilters({...filters, moveInDate: e.target.value})}
              />
              <select 
                className="p-3 border rounded-lg"
                value={filters.priceRange}
                onChange={(e) => setFilters({...filters, priceRange: e.target.value})}
              >
                <option value="">Price Range</option>
                <option value="0-500">$0 - $500</option>
                <option value="501-1000">$501 - $1000</option>
                <option value="1001+">$1001+</option>
              </select>
              <button className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white p-3 rounded-lg font-semibold hover:from-blue-600 hover:to-indigo-700">
                Search
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Results Grid */}
      <div className="container mx-auto px-6 py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {roommates.map((roommate) => (
            <div key={roommate.id} className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300">
              <img src={roommate.image} alt={roommate.name} className="w-full h-48 object-cover" />
              <div className="p-6">
                <h3 className="text-xl font-bold text-gray-800 mb-2">{roommate.name}</h3>
                <p className="text-gray-600 mb-4">{roommate.university}</p>
                <div className="flex flex-wrap gap-2 mb-4">
                  {roommate.interests.map((interest, index) => (
                    <span key={index} className="px-3 py-1 bg-blue-50 text-blue-600 rounded-full text-sm">
                      {interest}
                    </span>
                  ))}
                </div>
                <button className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-2 rounded-lg font-semibold hover:from-blue-600 hover:to-indigo-700">
                  View Profile
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default RoommateSearch;
