import { useAuth } from '../context/AuthContext';
import { Navigate } from 'react-router-dom';

function Dashboard() {
  const { user, isAuthenticated } = useAuth();

  // Redirect to login if not authenticated
  if (!isAuthenticated) {
    return <Navigate to="/signin" />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-gradient-to-br from-blue-600 via-blue-700 to-indigo-800 pt-8 pb-24">
        <div className="container mx-auto px-6">
          <h1 className="text-3xl font-bold text-white">Welcome, {user?.name}!</h1>
          <p className="text-blue-100 mt-2">Role: {user?.role}</p>
        </div>
      </div>

      <div className="container mx-auto px-6 -mt-16">
        <div className="bg-white rounded-2xl shadow-xl p-8">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Dashboard content based on user role */}
            {user?.role === 'user' && (
              <>
                <div className="bg-blue-50 p-6 rounded-xl">
                  <h3 className="text-xl font-semibold text-gray-800 mb-2">Find Roommates</h3>
                  <p className="text-gray-600">Search for compatible roommates based on your preferences.</p>
                </div>
                <div className="bg-blue-50 p-6 rounded-xl">
                  <h3 className="text-xl font-semibold text-gray-800 mb-2">Browse Listings</h3>
                  <p className="text-gray-600">Explore available housing options near your university.</p>
                </div>
              </>
            )}

            {user?.role === 'lister' && (
              <>
                <div className="bg-blue-50 p-6 rounded-xl">
                  <h3 className="text-xl font-semibold text-gray-800 mb-2">Manage Listings</h3>
                  <p className="text-gray-600">Add, edit, or remove your property listings.</p>
                </div>
                <div className="bg-blue-50 p-6 rounded-xl">
                  <h3 className="text-xl font-semibold text-gray-800 mb-2">View Inquiries</h3>
                  <p className="text-gray-600">Check and respond to housing inquiries.</p>
                </div>
              </>
            )}

            <div className="bg-blue-50 p-6 rounded-xl">
              <h3 className="text-xl font-semibold text-gray-800 mb-2">Profile Settings</h3>
              <p className="text-gray-600">Update your profile information and preferences.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
